import { getDownloadURL, getStorage, uploadBytes } from 'firebase/storage'
import { ref } from 'vue'
import { storage } from '../firebase/config'
import getUserAuth from './getUserAuth'

const { userAuth } = getUserAuth()

const useStorage = () => {
    const error = ref(null)
    const url = ref(null)
    const filePath = ref(null)

    const uploadImage = async (file) => {
        filePath.value = `users/${userAuth.value.uid}/${file.name}`
        const storageRef = ref(storage, `users/${userAuth.value.uid}/${file.name}`)

        try {
            //const res = await storageRef.put(file)
            const res = await uploadBytes(storageRef, file)
            //url.value = res.getDownloadURL()
            getDownloadURL(storageRef).then(url => {
                console.log(url)
            })
        } catch(err) {
            console.log(err.message)
            error.value = err.message
        }
    }

    return {
        error, url, filePath, uploadImage
    }
}

export default useStorage
